import { CollectTable, FormData, User, CollectionProject } from './models.js';
<template>
  <div class="data-collection">
    <!-- 数据采集表定义 -->
    <CollectTableDefinition v-if="showCollectTableDefinition" @save="saveCollectTable" @cancel="cancelCollectTableDefinition" />
    
    <!-- 数据采集 -->
    <DataCollection v-if="!showCollectTableDefinition && !showDataView" @finish="finishDataCollection" />
    
    <!-- 数据查看 -->
    <DataView v-if="showDataView" @back="backToDataCollection" />
    
    <!-- 用户管理 -->
    <UserManagement v-if="showUserManagement" @back="backToDataCollection" />
  </div>
</template>

<script>
import CollectTableDefinition from './CollectTableDefinition.vue';
import DataCollection from './DataCollection.vue';
import DataView from './DataView.vue';
import UserManagement from './UserManagement.vue';

export default {
  components: {
    CollectTableDefinition,
    DataCollection,
    DataView,
    UserManagement
  },
  data() {
    return {
      showCollectTableDefinition: false,
      showDataView: false,
      showUserManagement: false
    };
  },
  methods: {
    // 打开数据采集表定义界面
    openCollectTableDefinition() {
      this.showCollectTableDefinition = true;
    },
    // 保存数据采集表定义
    saveCollectTable(collectTable) {
      // 将数据采集表保存到后端
      console.log('保存数据采集表:', collectTable);
      this.showCollectTableDefinition = false;
    },
    // 取消数据采集表定义
    cancelCollectTableDefinition() {
      this.showCollectTableDefinition = false;
    },
    // 结束数据采集
    finishDataCollection() {
      // 提交数据到后端
      console.log('提交数据到后端');
      // 显示数据查看界面
      this.showDataView = true;
    },
    // 返回数据采集界面
    backToDataCollection() {
      this.showDataView = false;
      this.showUserManagement = false;
    },
    // 打开用户管理界面
    openUserManagement() {
      this.showUserManagement = true;
    }
  }
};
</script>


