import { createApp } from 'vue'  
import App from '../App.vue'  
import { createStore } from 'vuex' // 引入 Vuex 的 createStore 函数  
  
// 创建 Vuex 的 store 实例  
const store = createStore({  
  state: {  
    Title
  },  
  getters: {  
    // ... 定义你的 getter  
  },  
  mutations: {  
    // ... 定义你的 mutation  
  },  
  actions: {  
    // ... 定义你的 action  
  },  
  modules: {  
    // ... 定义你的模块  
  }  
})  
  
// 创建 Vue 应用实例  
const app = createApp(App)  
  
// 使用 store  
app.use(store)  
  
// 挂载应用到指定的元素上  
app.mount('#app')
