//App.vue充当整个应用程序的路由容器，<router-view></router-view>用来显示当前对应的组件内容
<template>
  <div class="app">
    <nav class="navbar">
      <ul class="nav-links">
        <li><router-link to="/" class="nav-item">Home</router-link></li>
        <li><router-link to="/login" class="nav-item">Login</router-link></li>
      </ul>
    </nav>
    <router-view></router-view>
  </div>
</template>

<style>
.app {
  font-family: Avenir, Helvetica, Arial, sans-serif;
  -webkit-font-smoothing: antialiased;
  -moz-osx-font-smoothing: grayscale;
  text-align: center;
  color: #2c3e50;
  margin-top: 60px;
}

.navbar {
  background-color: #5599ff; /* 导航栏背景颜色 */
  padding: 10px 0; /* 导航栏上下内边距 */
}

.nav-links {
  list-style: none; /* 去掉列表样式 */
  padding: 0;
  margin: 0;
  display: flex; /* 将链接排成一行 */
  justify-content: center; /* 居中对齐链接 */
}

.nav-item {
  margin: 0 10px; /* 间距 */
}

.nav-item a {
  color: #fff; /* 链接文本颜色 */
  text-decoration: none; /* 去掉下划线 */
  font-weight: bold; /* 字体加粗 */
  transition: color 0.3s; /* 链接颜色变化的动画 */
}

.nav-item a:hover {
  color: #ffc107; /* 鼠标悬停时链接文本颜色 */
}
</style>
